```
// First, let's clear the area
for (let x = -10; x <= 10; x++) {
    for (let z = -10; z <= 10; z++) {
        for (let y = 0; y <= 20; y++) {
            bot.setBlock(new Vec3(x, y, z), new Block(0));
        }
    }
}

// Now, let's build the castle walls
for (let x = -10; x <= 10; x++) {
    for (let z = -10; z <= 10; z++) {
        for (let y = 0; y <= 10; y++) {
            if (x === -10 || x === 10 || z === -10 || z === 10) {
                bot.setBlock(new Vec3(x, y, z), new Block(98)); // Stone Bricks
            }
        }
    }
}

// Add a gate
for (let y = 0; y <= 5; y++) {
    bot.setBlock(new Vec3(0, y, -10), new Block(0));
    bot.setBlock(new Vec3(1, y, -10), new Block(0));
}
```
